-- -----DROP DATABASE----- --
DROP DATABASE IF EXISTS dbdesignb;
-- -----CREATE DATABASE----- --
CREATE DATABASE dbdesignb;

use dbdesignb;
-- -----DROP TABLES----- --
DROP TABLE IF EXISTS tbl_items;
DROP TABLE IF EXISTS tbl_orders;
DROP TABLE IF EXISTS tbl_brands;

-- -----CREATE TABLEs----- --
CREATE TABLE IF NOT EXISTS tbl_brands(
brnd_name varchar(255) not null PRIMARY KEY,
brnd_description varchar(255) not null,
brnd_image blob
)ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS tbl_items(
item_id INT(10) not null AUTO_INCREMENT PRIMARY KEY,
item_name varchar(255) not null,
item_brand varchar(255) not null,
item_type char(1) not null,
item_material varchar(255) not null,
item_description varchar(255) not null,
item_price decimal(5,2) not null,
item_length int(3),
item_size varchar(5),
item_diameter decimal(3,2),
item_timestamp timestamp,
FOREIGN KEY fk_brand(item_brand)
REFERENCES tbl_brands (brnd_name)
ON UPDATE CASCADE
ON DELETE RESTRICT
)ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS tbl_orders(
ordr_id INT(10) not null AUTO_INCREMENT PRIMARY KEY,
ordr_item int(10) not null,
ordr_email varchar(255) not null,
ordr_address varchar(255) not null,
ordr_dateordered date not null,
ordr_quantity int(3) not null,
ordr_totalprice decimal (10,2),
ordr_status varchar(255),
ordr_timestamp timestamp,
FOREIGN KEY fk_item(ordr_item)
REFERENCES tbl_items(item_id)
ON UPDATE CASCADE
ON DELETE RESTRICT
)ENGINE=InnoDB;


